INSERT INTO "PUBLIC"."QUESTION" VALUES
(4742, 'Соблюдение кодекса этики и служебного поведения федерального государственного гражданского служащего ФССП России работниками службы судебных приставов учитывается:'),
(4743, 'Может ли подотчетное лицо, получив денежные средства в подотчет, передать их другому подотчетному лицу?'),
